import React, { useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";
import "./App.css";

const NEW_CHAT_MESSAGE_EVENT = "newChatMessage"; // Name of the event
const SOCKET_SERVER_URL = "http://localhost:6000";
const useChat = () => {
  const [messages, setMessages] = useState([]); // Sent and received messages
  const socketRef = useRef();

  useEffect(() => {

    // Creates a WebSocket connection
    socketRef.current = io(SOCKET_SERVER_URL);

    // Listens for incoming messages
    socketRef.current.on(NEW_CHAT_MESSAGE_EVENT, (message: any) => {
      const incomingMessage = {
        ...message,
        ownedByCurrentUser: message.senderId === socketRef.current.id,
      };
      setMessages((messages: any) => [...messages, incomingMessage]);
    });

    // Destroys the socket reference
    // when the connection is closed
    return () => {
      socketRef.current.disconnect();
    };
  }, []);

  // Sends a message to the server that
  // forwards it to all users in the same room
  const sendMessage = (messageBody: any) => {
    socketRef.current.emit(NEW_CHAT_MESSAGE_EVENT, {
      body: messageBody,
      senderId: socketRef.current.id,
    });
  };

  return { messages, sendMessage };
};


function App() {

  const { messages, sendMessage } = useChat();
  console.log(messages);

  return (
    <section className="main">
      <div className="container">
        <div className="row">
          <div className="col"></div>
          <div className="col text"></div>
          <div className="col"></div>
        </div>
        <div className="row">
          <div className="col text"></div>
          <div className="col marker">
            <svg
              width="144"
              height="150"
              viewBox="0 0 144 150"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <rect width="143.895" height="150" fill="url(#pattern0)" />
              <defs>
                <pattern
                  id="pattern0"
                  patternContentUnits="objectBoundingBox"
                  width="1"
                  height="1"
                >
                  <use
                    href="#image0"
                    transform="translate(-0.0212121) scale(0.0115825 0.0111111)"
                  />
                </pattern>
                <image
                  id="image0"
                  width="90"
                  height="90"
                  href="data:image/png;base64,"
                />
              </defs>
            </svg>
          </div>
          <div className="col text"></div>
        </div>
        <div className="row">
          <div className="col"></div>
          <div className="col text"></div>
          <div className="col"></div>
        </div>
      </div>

      <div className="predictive">
        <div className="predictive__predict-word left">Hello</div>
        <div className="predictive__predict-word text"></div>
        <div className="predictive__predict-word right">Hi</div>
      </div>
    </section>
  );
}

export default App;
